var title_f33_54_34668="Pediatric oropharyngeal trauma assessment and imaging";
var content_f33_54_34668=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F80771&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F80771&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Oropharyngeal trauma physical assessment and suggested imaging",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Finding",
"       </td>",
"       <td class=\"subtitle1\">",
"        Minor",
"       </td>",
"       <td class=\"subtitle1\">",
"        Moderate",
"       </td>",
"       <td class=\"subtitle1\">",
"        Severe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mechanism",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Low force:",
"         </strong>",
"         eg,",
"        </p>",
"        <p>",
"         Fall from sitting with toy in the mouth",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Intermediate force:",
"         </strong>",
"         eg,",
"        </p>",
"        <p>",
"         Fall while running with pencil in mouth",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Bicycle accident while eating a popsicle",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          High force:",
"         </strong>",
"         eg,",
"        </p>",
"        <p>",
"         High speed motor vehicle accident with impaling oropharyngeal object",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Penetrating injury with knife or gunshot wound",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wound appearance",
"       </td>",
"       <td>",
"        <p>",
"         Mucosal laceration &lt;2 cm in length",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Superficial midline puncture wound",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Submucosal laceration &gt;2 cm in length",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Tonsillar or lateral soft palate deep or indeterminate depth puncture wound",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Protruding or retained foreign body*",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Large, gaping wound with copious bleeding",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         As for moderate injury with associated neurovascular findings",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Associated findings",
"       </td>",
"       <td>",
"        Superficial gum laceration or abrasion",
"       </td>",
"       <td>",
"        <p>",
"         Tooth fracture, luxation, or avulsion",
"        </p>",
"        <p>",
"         Tongue laceration",
"        </p>",
"        <p>",
"         Jaw fracture",
"        </p>",
"       </td>",
"       <td>",
"        Neurologic abnormality carotid artery bruit on side of injury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Radiographic imaging",
"       </td>",
"       <td>",
"        Not indicated",
"       </td>",
"       <td>",
"        CTA or contrast enhanced MRA&bull;",
"       </td>",
"       <td>",
"        Digital subtraction CAA",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CTA: computed tomography angiography; MRA: magnetic resonance angiography; CAA: carotid artery angiogram.",
"     <br>",
"      * A protruding oropharyngeal foreign body should only be removed in the operating room.",
"      <br>",
"       &bull; Contrast enhanced MRA is preferred if available and if the patient can cooperate with not moving for 30 to 60 minutes.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
