var title_f33_57_34717="History reading difficulty";
var content_f33_57_34717=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F58064&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F58064&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Important aspects of the history in a child with reading difficulty",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Feature",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical implications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Perinatal history",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Maternal drug or alcohol intake",
"       </td>",
"       <td>",
"        May cause neurologic problems",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Congenital infections",
"       </td>",
"       <td>",
"        May cause neurologic or hearing problems",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Gestational age, birth weight",
"       </td>",
"       <td>",
"        Premature birth is a risk for learning disability",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Medical history",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Recurrent or persistent ear infections",
"       </td>",
"       <td>",
"        May be associated with hearing impairment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Iron deficiency",
"       </td>",
"       <td>",
"        Associated with neurologic deficits",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lead poisoning",
"       </td>",
"       <td>",
"        Associated with neurologic deficits and hearing impairment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Seizures",
"       </td>",
"       <td>",
"        May be associated with neurologic problems",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Frequent injuries",
"       </td>",
"       <td>",
"        May indicate coordination difficulties, history of closed head injury, poor supervision, and/or environmental deprivation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chronic illness or medication usage",
"       </td>",
"       <td>",
"        May be associated with side effects or school absences with secondary effects on learning",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Developmental history",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Delayed or disordered acquisition of language milestones and communication skills",
"       </td>",
"       <td>",
"        Language delay is a risk for reading difficulty",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Uneven pattern of skills and interests",
"       </td>",
"       <td>",
"        May indicate learning disability",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Behavioral history",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Problems with attention, impulsivity, overactivity, acting out",
"       </td>",
"       <td>",
"        Behavioral problems may affect learning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sadness",
"       </td>",
"       <td>",
"        Emotional problems may affect learning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Poor self-esteem",
"       </td>",
"       <td>",
"        May be a consequence of reading difficulty",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Social history",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Child abuse or neglect, or other social stressors (eg, homelessness, poverty, illness in family member)",
"       </td>",
"       <td>",
"        These stressors may affect learning",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Family history",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Learning problems, reading or spelling problems, avoidance of reading, school failure among first-degree relatives",
"       </td>",
"       <td>",
"        Increases risk of reading disability",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        School history",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\" colspan=\"2\">",
"        Academic achievement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Best and worst subjects",
"       </td>",
"       <td>",
"        Children with reading disability may excel in other areas or areas of interest (eg, math, science)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        History of grade retention",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\" colspan=\"2\">",
"        Classroom behavior",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Long-standing problems with attention, impulsivity, overactivity",
"       </td>",
"       <td>",
"        Reading disability is associated with attention deficit hyperactivity disorder, which can further interfere with school function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Depression, moodiness",
"       </td>",
"       <td>",
"        Emotional problems may affect learning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Behavior differs in reading versus other subjects",
"       </td>",
"       <td>",
"        Children with reading difficulty may \"act out\" during activities that require reading",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\" colspan=\"2\">",
"        Attendance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Excessive absenteeism",
"       </td>",
"       <td>",
"        Frequent absences may affect learning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        School avoidance",
"       </td>",
"       <td>",
"        Children with reading difficulty may try to avoid school",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\" colspan=\"2\">",
"        Past psychoeducational testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Discrepancy between cognitive abilities and academic achievement",
"       </td>",
"       <td>",
"        Suggestive of a learning disability",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\" colspan=\"2\">",
"        Special educational services",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Response to educational interventions",
"       </td>",
"       <td>",
"        Lack of response suggests a learning disability rather than lack of exposure to reading and or inadequate instruction before formal schooling",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
