var title_f33_60_34763="Grading system predicting vascular invasion by pancreatic cancer";
var content_f33_60_34763=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F83537&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F83537&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Grading system* for predicting vascular invasion by a pancreatic cancer on dual-phase contrast-enhanced CT",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Category",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Grade 0",
"       </td>",
"       <td>",
"        No contiguity of tumor with a vessel",
"       </td>",
"       <td>",
"        Vascular invasion in 0 percent of cases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Grade 1",
"       </td>",
"       <td>",
"        Tumor contiguous with &lt;25 percent of the circumference of a vessel",
"       </td>",
"       <td>",
"        Vascular invasion in 0 percent of cases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Grade 2",
"       </td>",
"       <td>",
"        Tumor contiguous with 25 to 50 percent of the circumference of a vessel",
"       </td>",
"       <td>",
"        Vascular invasion in 57 percent of cases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Grade 3",
"       </td>",
"       <td>",
"        Tumor contiguous with 50 to 75 percent of the circumference of a vessel",
"       </td>",
"       <td>",
"        Vascular invasion in 88 percent of cases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Grade 4",
"       </td>",
"       <td>",
"        Tumor contiguous with &gt;75 percent of the circumference of a vessel or any vessel constriction",
"       </td>",
"       <td>",
"        Vascular invasion in all cases",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    * Proposed by Lu DSK, et al. AJR Am J Roentgenol 1997; 168:1439.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Wong JC, Raman S. Surgical resectability of pancreatic adenocarcinoma: CTA. Abdom Imaging 2010; 35:471. Copyright &copy; 2010; with kind permission from Springer Science + Business Media B.V.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
