" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Definitions for estimating magnitude of effects",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Size of effect",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Small/slight",
"       </td>",
"       <td>",
"        <strong>",
"         Pain scales:",
"        </strong>",
"        Mean 5-10 mm improvement on a 100 mm visual analogue scale (VAS), or equivalent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Back-specific functional status:",
"        </strong>",
"        Mean 5-10 mm improvement on the Oswestry Disability Index (ODI), 1-2 points on the Roland-Morris Disability Questionnaire (RDQ), or equivalent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         All outcomes:",
"        </strong>",
"        Standardized mean difference (SMD) 0.2 to 0.5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Moderate",
"       </td>",
"       <td>",
"        <strong>",
"         Pain scales:",
"        </strong>",
"        Mean 10-20 mm improvement on a 100 mm VAS, or equivalent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Back-specific functional status:",
"        </strong>",
"        Mean 10-20 mm improvement on the ODI, 2-5 points on the RDQ, or equivalent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         All outcomes:",
"        </strong>",
"        SMD 0.5 to 0.8",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Large/substantial",
"       </td>",
"       <td>",
"        <strong>",
"         Pain scales:",
"        </strong>",
"        Mean &gt;20 mm improvement on a 100 mm VAS, or equivalent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Back-specific functional status:",
"        </strong>",
"        Mean &gt;20 mm improvement on the ODI, &gt;5 points on the RDQ, or equivalent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         All outcomes:",
"        </strong>",
"        SMD &gt;0.8",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
