var title_f33_7_33915="Automatic external defibrillator use in children";
var content_f33_7_33915=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F74523&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F74523&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Key points regarding the use of Automatic External Defibrillators (AED) in infants and children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        &bull; VF is the initial rhythm in 9 to 19 percent of children experiencing out-of-hospital cardiac arrests.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Survival with VF is better than survival with asystole.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Defibrillation is indicated for the treatment of VF and pulseless VT.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; AEDs are computerized machines that automatically diagnose VF and use voice prompts to instruct rescuers to",
"        <strong>",
"         defibrillate",
"        </strong>",
"        , if appropriate.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; AEDs have demonstrated both high sensitivity in detecting VF and high specificity in identifying nonshockale rhythms in children.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; For infants and children with VF or pulseless VT, shock delivery may be accomplished by the following devices (in order of preference):",
"        <br/>",
"        <ol>",
"         <li>",
"          Manual defibrillator (when used by a trained provider)",
"         </li>",
"         <li>",
"          AED with a dose attenutation device",
"         </li>",
"         <li>",
"          AED without a dose attenuation device",
"         </li>",
"        </ol>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; For in-hospital defibrillation, there are no data comparing manual defibrillators with AEDs or clear guidelines specifying the use of one over the other.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; AEDs cannot be used for cardioversion.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     VF: ventricular fibrillation; VT: ventricular tachycardia.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
