var title_f33_8_33931="PAIN RULES mnemonic for palliative care";
var content_f33_8_33931=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F74629&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F74629&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    PAIN RULES mnemonic for assessing symptomatology in palliative care*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <strong>",
"         P",
"        </strong>",
"        ain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         A",
"        </strong>",
"        norexia and other appetite or oral intake related issues",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         I",
"        </strong>",
"        ncontinence and other genitourinary symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         N",
"        </strong>",
"        ausea and other gastrointestinal symptoms (constipation, vomiting, diarrhea)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        <span class=\"small\">",
"         &nbsp;",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         R",
"        </strong>",
"        espiratory symptoms (dyspnea, cough)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         U",
"        </strong>",
"        lcerations and other skin complaints",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         L",
"        </strong>",
"        evel of functioning (expressed, for instance, as ECOG or Karnofsky, or other performance scales)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         E",
"        </strong>",
"        nergy and other related issues such as fatigue or asthenia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         S",
"        </strong>",
"        edation, sleep and other side effects of treatment (including opioids and chemotherapy)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ECOG: Eastern Cooperative Oncology Group.",
"     <br>",
"      * Prevalence of symptoms derived from J Pain Symptom Manage 2006; 31:58.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
