var content_f34_10_34988=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F73892&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F73892&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Summary of ACIP recommendations on use of immune globulins in immune compromised persons",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Immune globulin",
"       </td>",
"       <td class=\"subtitle1\">",
"        Not immunocompromised",
"       </td>",
"       <td class=\"subtitle1\">",
"        HIV infected",
"       </td>",
"       <td class=\"subtitle1\">",
"        Severely immunocompromised*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Immune globulin",
"       </td>",
"       <td rowspan=\"2\">",
"        Recommended for infants and adults with contraindication to measles vaccine exposed to measles",
"       </td>",
"       <td>",
"        Recommended for symptomatic patients regardless of immunization status",
"       </td>",
"       <td rowspan=\"2\">",
"        Recommended for patients exposed to measles regardless of immunization status",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recommended for persons with exposure to hepatitis A or who will travel to HAV-endemic areas",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        Varicella zoster immune globulin",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        Recommended for newborns of mothers who develop chicken pox within 5 days before and 48 hours after delivery",
"       </td>",
"       <td rowspan=\"4\">",
"        Recommended for susceptible infants and adults after significant exposure to V-Z",
"       </td>",
"       <td rowspan=\"4\">",
"        Recommended for susceptible infants and adults after significant exposure to V-Z",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recommended for exposed newborns (&lt;28 weeks gestation of susceptible mothers)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recommended for exposed pre-term infants &lt;28 weeks or &lt;1000 gm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May be used for exposed, susceptible adults, exposed pregnant women and infants &lt;28 days",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Tetanus IG",
"       </td>",
"       <td>",
"        Recommended for those with serious wounds and &lt;3 doses of tetanus toxoid",
"       </td>",
"       <td>",
"        Same as for non-immunocompromised",
"       </td>",
"       <td>",
"        Same as for non-immunocompromised",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Hepatitis B IG",
"       </td>",
"       <td>",
"        Recommended for prophylaxis of infants born to HBsAg+ mothers and persons with percutaneous, sexual, or mucosal exposure to HB virus",
"       </td>",
"       <td>",
"        Same as for non-immunocompromised",
"       </td>",
"       <td>",
"        Same as for non-immunocompromised",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Rabies IG",
"       </td>",
"       <td>",
"        Recommended for post- exposure prophylaxis of persons not previously vaccinated against rabies",
"       </td>",
"       <td>",
"        Same as for non-immunocompromised",
"       </td>",
"       <td>",
"        Same as for non-immunocompromised",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Severe immunosuppression can be the result of congenital immunodeficiency, HIV infection, leukemia, lymphoma, aplastic anemia, generalized malignancy or therapy with alkylating agents, antimetabolites, radiation, or large amounts of corticosteroids.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     VZIG is no longer available. VariZIG became available in 2006.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: CDC. Recommendations of the advisory committee on immunization practices (ACIP): use of vaccines and immune globulins for persons with altered immune competence. MMWR 2006; 55RR15:1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
