" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pneumococcal pneumonia in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/11/35001/contributors\">",
