"  <h1>",
"   TOPIC OUTLINE",
"  </h1>",
"  <div id=\"outline\">",
"   <ul>",
"    <li>",
"     <a class=\"sr_button\" href=\"#H5106412\" id=\"summRecButton\">",
"      <span>",
"       SUMMARY",
"      </span>",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H1\">",
"      INTRODUCTION AND OVERVIEW",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H2\">",
"      IL-1 INHIBITION",
