var title_f34_16_35083="Classification and characterization of proteinuria types";
var content_f34_16_35083=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F85791&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F85791&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification and characterization of proteinuria types",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Classification of proteinuria",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical setting",
"       </td>",
"       <td class=\"subtitle1\">",
"        Typical level of proteinuria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Transient proteinuria",
"       </td>",
"       <td>",
"        Fever, heavy exercise, vasopressor infusion, albumin infusion",
"       </td>",
"       <td>",
"        &lt;1 g/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persistent proteinuria - orthostatic proteinuria",
"       </td>",
"       <td>",
"        Uncommon over age 30 years, may occur in 2 to 5 percent of adolescents",
"       </td>",
"       <td>",
"        &lt;1 to 2&nbsp;g/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persistent proteinuria - overflow proteinuria",
"       </td>",
"       <td>",
"        Myeloma (monoclonal light chains), Hemolysis (hemoglobinuria), Rhabdomyolysis (myoglobinuria)",
"       </td>",
"       <td>",
"        Variable, could be nephrotic range",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persistent proteinuria - glomerular proteinuria",
"       </td>",
"       <td>",
"        Primary glomerular diseases, secondary glomerular diseases, diabetic nephropathy, hypertensive nephrosclerosis",
"       </td>",
"       <td>",
"        Variable, often nephrotic range",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persistent proteinuria - tubulointerstitial proteinuria",
"       </td>",
"       <td>",
"        Heavy metal intoxications, autoimmune or allergic interstitial inflammation, medication-induced interstitial injury",
"       </td>",
"       <td>",
"        &lt;3 g/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Post-renal proteinuria",
"       </td>",
"       <td>",
"        Urinary tract infections, nephrolithiasis, genito-urinary tumor",
"       </td>",
"       <td>",
"        &lt;1 g/day",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
