var title_f34_16_35084="Treatment of giardiasis";
var content_f34_16_35084=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F61810&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F61810&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of giardiasis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        Dosage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Adults",
"       </td>",
"       <td class=\"subtitle2\">",
"        Children",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Drugs of choice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tinidazole",
"       </td>",
"       <td>",
"        2 grams orally, single dose",
"       </td>",
"       <td>",
"        50 mg/kg orally, single dose (maximum dose 2 grams)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Metronidazole",
"       </td>",
"       <td>",
"        500&nbsp;mg orally twice daily, OR 250 mg orally three times per day; duration 5 to 7 days",
"       </td>",
"       <td>",
"        5 to 10&nbsp;mg/kg orally three times per day for 7 days (maximum 250 mg per dose)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nitazoxanide",
"       </td>",
"       <td>",
"        500 mg orally two times per day for three days",
"       </td>",
"       <td>",
"        <p>",
"         Age 1-3 years: 100 mg orally two times per day for 3 days",
"        </p>",
"        <p>",
"         Age 4-11 years: 200 mg orally two times per day for 3 days",
"        </p>",
"        <p>",
"         Age over 11 years: Same as adult dose",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Alternative agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Albendazole",
"       </td>",
"       <td>",
"        <p>",
"         400 mg orally once daily for 5 days",
"        </p>",
"       </td>",
"       <td>",
"        10 to 15 mg/kg orally once daily for 5 days (maximum 400 mg per dose)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mebendazole",
"       </td>",
"       <td>",
"        <p>",
"         200 mg orally three times per day for 5 days",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         200 mg orally three times per day for 5 days",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Paromomycin*",
"       </td>",
"       <td>",
"        10 mg/kg orally three times per day for 5 to 10 days",
"       </td>",
"       <td>",
"        <p>",
"         10 mg/kg orally three times per day for 5 to 10 days",
"        </p>",
"        <p>",
"         &nbsp;",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Furazolidone",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        100 mg orally four times per day for 7 to 10 days",
"       </td>",
"       <td>",
"        1.5 mg/kg orally four times per day for 7 to 10 days (maximum 100 mg per dose)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Quinacrine",
"       </td>",
"       <td>",
"        100 mg orally three times per day for 5 days",
"       </td>",
"       <td>",
"        2 mg/kg orally three times per day for&nbsp;5 days (maximum 100 mg per dose)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    See text for additional information regarding choice of therapy, approach to recurrent infection, and availability.",
"    <div class=\"footnotes\">",
"     * Poor intestinal absorption; may be useful for treatment of giardiasis in pregnancy.",
"     <br>",
"      <font class=\"bullet\">",
"       &bull;",
"      </font>",
"      Potentially significant drug and food interactions with use greater than 5 days due to monoamine oxidase inhibition. For additional information (see \"Furazolidone: Drug information\" section 'Drug Interactions').",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
