var title_f34_17_35099="Patch test interpretation";
var content_f34_17_35099=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F65159&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F65159&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Interpretation of patch test reactions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Score",
"       </td>",
"       <td class=\"subtitle1\">",
"        Interpretation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Tips and advice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        Negative reaction",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        ? or +/-",
"       </td>",
"       <td>",
"        Doubtful reaction, faint erythema",
"       </td>",
"       <td>",
"        Caution with interpretation, especially with less common allergens. Consider repeat testing or follow-up with ROAT testing.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        Weak reaction, erythema, slight infiltration",
"       </td>",
"       <td>",
"        Erythema should be palpable and occupy &gt;50 percent of patch test site.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        ++",
"       </td>",
"       <td>",
"        Strong reaction, erythema, infiltration, vesicles",
"       </td>",
"       <td>",
"        Look for microvesicles to occupy at least 50 percent of the patch test site.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        +++",
"       </td>",
"       <td>",
"        Extreme, bullous, ulcerative",
"       </td>",
"       <td>",
"        Confluent vesicles.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IR",
"       </td>",
"       <td>",
"        Irritant reactions",
"       </td>",
"       <td>",
"        There is a broad range. Mild irritant reactions have a glazed appearance. Moderate reactions are follicular (pustular in atopics). Extreme reactions can be ulcerative.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        NT",
"       </td>",
"       <td>",
"        Not tested",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
