var title_f34_20_35148="Criteria for preeclampsia";
var content_f34_20_35148=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F79977%7EOBGYN%2F76975&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F79977%7EOBGYN%2F76975&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for the diagnosis of preeclampsia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Systolic blood pressure &ge;140 mmHg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diastolic blood pressure &ge;90 mmHg*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Proteinuria &ge;0.3 grams in a 24-hour urine specimen",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    The elevation in blood pressure should be sustained, which is generally regarded as two measurements at least six hours, but no more than seven days, apart.",
"    <div class=\"footnotes\">",
"     * Diastolic blood pressure is determined based upon the fifth Korotkoff sound (disappearance) with patient sitting.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     A random urine protein determination of 30 mg/dL or 1+ on dipstick is suggestive, but not diagnostic, of the presence of this criterion.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    The presence of one or more of the following criteria upstages preeclampsia from mild to severe",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Symptoms of central nervous system dysfunction:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <p>",
"         Visual disturbance (photopsia, scotomata, cortical blindness, retinal vasospasm)",
"        </p>",
"        <p>",
"         Severe headache (ie, incapacitating, \"the worst headache I've ever had\") or headache that persists and progresses despite analgesic therapy",
"        </p>",
"        <p>",
"         Altered mental status&nbsp;",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Symptoms of liver capsule distention:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Right upper quadrant or epigastric pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Nausea, vomiting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Hepatocellular injury:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Serum transaminase concentration &ge; twice normal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Severe blood pressure elevation:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Systolic blood pressure &ge;160 mm Hg or diastolic blood pressure&nbsp;&ge;110 mm Hg on two occasions at least six hours apart",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Thrombocytopenia:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &lt;100,000 platelets/microL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Proteinuria:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &ge;5 grams in 24 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oliguria &lt;500 mL in 24 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fetal growth restriction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary edema or cyanosis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Modified from&nbsp;Diagnosis and Management of Preeclampsia and Eclampsia. American College of Obstetricians and Gynecologists. ACOG Practice Bulletin #33, January 2002.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
