" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Metabolic emergencies at birth",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?34/23/35188/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?34/23/35188/contributors\">",
"     Jochen Profit, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?34/23/35188/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?34/23/35188/contributors\">",
"     Leonard E Weisman, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
