" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Basic properties of myocardial perfusion agents",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?34/23/35192/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?34/23/35192/contributors\">",
"     Thomas A Holly, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?34/23/35192/contributors\">",
"     Preeti Kansal, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?34/23/35192/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?34/23/35192/contributors\">",
"     Warren J Manning, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?34/23/35192/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?34/23/35192/contributors\">",
"     Brian C Downey, MD, FACC",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"UTD.htm?34/23/35192/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
