var title_f34_23_35195="Adverse effects of dapsone";
var content_f34_23_35195=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F86540&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F86540&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Adverse effects of dapsone",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"20%\">",
"     </colgroup>",
"     <colgroup width=\"60%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Adverse effect",
"       </td>",
"       <td class=\"subtitle1\">",
"        Frequency",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Hemolysis",
"       </td>",
"       <td>",
"        Common",
"       </td>",
"       <td>",
"        Usually a compensated hemolysis but can be severe. Need to monitor with routine complete blood counts.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Agranulocytosis",
"       </td>",
"       <td>",
"        Uncommon",
"       </td>",
"       <td>",
"        Can be severe and life-threatening. Usually occurs in the first few months of treatment.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Methemoglobinemia",
"       </td>",
"       <td>",
"        Common",
"       </td>",
"       <td>",
"        Usually asymptomatic. Symptomatic methemoglobinemia (cardiopulmonary symptoms) should be evaluated and requires alteration of dose or discontinuation of dapsone.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Hypersensitivity syndrome (DRESS)",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Life-threatening. Fever, lymphadenopathy, eosinophilia, flu-like symptoms, hepatitis, morbilliform skin eruption. Immediately discontinue dapsone.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peripheral neuropathy",
"       </td>",
"       <td>",
"        Uncommon",
"       </td>",
"       <td>",
"        Usually seen in patients with high daily doses of dapsone and high cumulative doses. Can be motor, sensory, or mixed neuropathy.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     DRESS: drug rash with eosinophilia and systemic symptoms.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
