var title_f34_23_35197="Gilliam classification skin LE";
var content_f34_23_35197=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F77275&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F77275&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Gilliam classification of skin lesions associated with LE",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Specific skin disease (cutaneous LE [CLE])",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Acute cutaneous LE [ACLE]",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            1. Localized ACLE (malar rash; butterfly rash)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            2. Generalized ACLE (lupus, maculopaoular rash, SLE rash, photosensitive lupus dermatitis)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Subacute cutaneous LE [SCLE]",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            1. Annular SCLE (lupus marginatus, symmetric erythema centrifugum, autoimmune annular erythema, lupus erythematosus gyratus repens)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            2. Papulosquamous SCLE (disseminated DLE, subacute disseminated LE, superficial disseminated LE, psoriasiform LE, pityriasiform LE and maculopapular photosensitive LE)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Chronic cutaneous LE (CCLE)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2_start\">",
"            1. Classic discoid LE [DLE]",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            a. Localized DLE",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            b. Generalized DLE",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            2. Hypertrophic/verrucous DLE",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            3. Lupus profundus/lupus panniculitis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2_start\">",
"            4. Mucosal DLE",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            a. Oral DLE",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            b. Conjunctival DLE",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            5. Lupus tumidus (urticarial plaque of LE)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            6. Chilblains LE (chilblains lupus)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            7. Lichenoid DLE (LE/lichen planus overlap, lupus planus) nonspecific skin disease",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Cutaneous vascular disease",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist1_start\">",
"            1. Vasculitis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist1\">",
"            a. Leukocytoclastic",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            (1) Palpable purpura",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            (2) Urticarial vasculitis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist1\">",
"            b. Periarteritis nodosa-like cutaneous lesions",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist1_start\">",
"            2. Vasculopathy",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist1\">",
"            a. Degos' disease-like lesions",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist1\">",
"            b. Secondary atrophie blanche (livedoid vasculitis, livedo vasculitis)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            3. Periungual telangiectasia",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            4. Livedo reticularis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            5. Thrombophlebitis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            6. Raynaud's phenomenom",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            7. Erythromelalgia (erythermalgia)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Nonscarring alopecia",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            1. \"Lupus hair\"",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            2. Telogen effluvium",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            3. Alopecia areata",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Other cutaneous manifestations",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Sclerodactly",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Rheumatoid nodules",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Calcinosis cutis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            LE-nonspecific bullous lesions",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Urticaria",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Papulonodular mucinosis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Cutis laxa/anetoderma",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Acanthosis nigricans (type B insulin resistance)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Erythema multiforme",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Leg ulcers",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Lichen planus",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     LE: Lupus erythematosus",
"     <br>",
"      Alternative or synonymous terms are listed in parentheses; abbreviations are indicated in brackets.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Sontheimer, RD. The lesions of cutaneous lupus erythematosus-a review and personal perspective on nomenclature and classification of cutaneous manifestations of lupus erythematosus. Lupus 1997; 6:84. Copyright &copy;1997 Arnold Publishers.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
