var script_f34_24_35201=[" ",
"",
"var calctxt = '';",
"var xmltxt = '';",
"var htmtxt = '';",
"",
"",
"function IPINHL_fx() {",
"with(document.IPINHL_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1.checked){",
"Score = Score + 1;",
"}",
"if (cc2.checked){",
"Score = Score + 1;",
"}",
"if (cc3.checked){",
"Score = Score + 1;",
"}",
"if (cc4.checked){",
"Score = Score + 1;",
"}",
"if (cc5[0].checked){",
"Score = Score + 0;",
"}",
"if (cc5[1].checked){",
"Score = Score + 0;",
"}",
"if (cc5[2].checked){",
"Score = Score + 1;",
"}",
"if (cc5[3].checked){",
"Score = Score + 1;",
"}",
"if (cc5[4].checked){",
"Score = Score + 1;",
"}",
"cctotal.value = Score;",
"",
"if (doCalc){",
"rrclr();",
"if (( Score >= 0) && ( Score <= 1)){ document.getElementById('rr1_1').bgColor = '#cccccc';",
"}",
"if (( Score >= 2) && ( Score <= 2)){ document.getElementById('rr1_2').bgColor = '#cccccc';",
"}",
"if (( Score >= 3) && ( Score <= 3)){ document.getElementById('rr1_3').bgColor = '#cccccc';",
"}",
"if (( Score >= 4) && ( Score <= 5)){ document.getElementById('rr1_4').bgColor = '#cccccc';",
"}",
"if ((Score >= 0) && (Score <= 1)){ document.getElementById('rr2_5').bgColor = '#cccccc';",
"}",
"if ((Score >= 2) && (Score <= 2)){ document.getElementById('rr2_6').bgColor = '#cccccc';",
"}",
"if ((Score >= 3) && (Score <= 3)){ document.getElementById('rr2_7').bgColor = '#cccccc';",
"}",
"if ((Score >= 4) && (Score <= 5)){ document.getElementById('rr2_8').bgColor = '#cccccc';",
"}",
"",
"}",
"}",
"}",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"document.getElementById('rr1_4').bgColor = \"\";",
"document.getElementById('rr2_5').bgColor = \"\";",
"document.getElementById('rr2_6').bgColor = \"\";",
"document.getElementById('rr2_7').bgColor = \"\";",
"document.getElementById('rr2_8').bgColor = \"\";",
"}",
"",
"",
""].join("\n");
