var title_f34_24_35212="Perioperative thrombotic risk";
var content_f34_24_35212=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F86930&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F86930&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Perioperative thrombotic risk",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Risk stratum",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"3\">",
"        Indication for VKA therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Mechanical heart valve",
"       </td>",
"       <td class=\"subtitle2\">",
"        Atrial fibrillation",
"       </td>",
"       <td class=\"subtitle2\">",
"        VTE",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High thrombotic risk*",
"       </td>",
"       <td>",
"        <p>",
"         Any mitral valve prosthesis",
"        </p>",
"        <p>",
"         Any caged-ball or tilting disc aortic valve prosthesis",
"        </p>",
"        Recent (within six months) stroke or transient ischemic attack",
"       </td>",
"       <td>",
"        <p>",
"         CHADS",
"         <sub>",
"          2",
"         </sub>",
"         score of 5 or 6",
"        </p>",
"        <p>",
"         Recent (within three months) stroke or transient ischemic attack",
"        </p>",
"        Rheumatic valvular heart disease",
"       </td>",
"       <td>",
"        <p>",
"         Recent (within three months) VTE",
"        </p>",
"        Severe thrombophilia (eg, deficiency of protein C, protein S, or antithrombin; antiphospholipid antibodies; multiple abnormalities)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Moderate thrombotic risk",
"       </td>",
"       <td>",
"        Bileaflet aortic valve prosthesis and one or more of the of following risk factors: atrial fibrillation, prior stroke or transient ischemic attack, hypertension, diabetes, congestive heart failure, age &gt;75 years",
"       </td>",
"       <td>",
"        CHADS",
"        <sub>",
"         2",
"        </sub>",
"        score of 3 or 4",
"       </td>",
"       <td>",
"        <p>",
"         VTE within the past 3 to 12 months",
"        </p>",
"        <p>",
"         Nonsevere thrombophilia (eg, heterozygous factor V Leiden or prothrombin gene mutation)",
"        </p>",
"        <p>",
"         Recurrent VTE",
"        </p>",
"        Active cancer (treated within six months or palliative)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low thrombotic risk",
"       </td>",
"       <td>",
"        Bileaflet aortic valve prosthesis without atrial fibrillation and no other risk factors for stroke",
"       </td>",
"       <td>",
"        CHADS",
"        <sub>",
"         2",
"        </sub>",
"        score of 0 to 2 (assuming no prior stroke or transient ischemic attack)",
"       </td>",
"       <td>",
"        VTE &gt;12 months previous and no other risk factors",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     VKA: vitamin K antagonist; VTE: venous thromboembolism; CHADS",
"     <sub>",
"      2",
"     </sub>",
"     : congestive heart failure, hypertension, age &ge;75 years, diabetes mellitus, and stroke or transient ischemic attack.",
"     <br/>",
"     * High risk patients may also include those with a prior stroke or transient ischemic attack occurring &gt;3 months before the planned surgery and a CHADS",
"     <sub>",
"      2",
"     </sub>",
"     score &lt;5, those with prior thromboembolism during temporary interruption of VKAs, or those undergoing certain types of surgery associated with an increased risk for stroke or other thromboembolism (eg, cardiac valve replacement, carotid endarterectomy, major vascular surgery).",
"    </div>",
"    <div class=\"reference\">",
"     From: Douketis JD, Spyropoulos AC, Spencer FA, et al. Perioperative management of antithrombotic therapy: Antithrombotic Therapy and Prevention of Thrombosis, 9th ed: American College of Chest Physicians Evidence-Based Clinical Practice Guidelines. Chest 2012; 141(2 Suppl):e326S. Copyright &copy; 2012. Reproduced with permission from the American College of Chest Physicians.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
