" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Early noncardiac complications of coronary artery bypass graft surgery",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/25/35226/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/25/35226/contributors\">",
