var title_f34_25_35227="ESC recommendations carotid sinus massage";
var content_f34_25_35227=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F69426&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F69426&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    European Society of Cardiology recommendations for carotid sinus massage",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Recommendations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Class*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Level",
"        <span class=\"small\">",
"         &bull;",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Indications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        CSM is indicated in patients &gt;40 years with syncope of unknown aetiology after initial evaluation",
"       </td>",
"       <td>",
"        I",
"       </td>",
"       <td>",
"        B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        CSM should be avoided in patients with previous TIA or stroke within the past&nbsp;three months and in patients with carotid bruits (except if carotid Doppler studies excluded significant stenosis)",
"       </td>",
"       <td>",
"        III",
"       </td>",
"       <td>",
"        C",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Diagnostic criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        CSM is diagnostic if syncope is reproduced in the presence of asystole longer than 3 s and/or a fall in systolic BP &gt;50 mmHg",
"       </td>",
"       <td>",
"        I",
"       </td>",
"       <td>",
"        B",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     BP: blood pressure; CSM: carotid sinus massage; TIA: transient ischaemic attack.",
"     <br>",
"      * Class of recommendation.",
"      <br>",
"       <span class=\"bullet\">",
"        &bull;",
"       </span>",
"       Level of evidence.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: European Heart Rhythm Association (EHRA), Heart Failure Association (HFA), Heart Rhythm Society (HRS), et al. Guidelines for the diagnosis and management of syncope (version 2009): the Task Force for the Diagnosis and Management of Syncope of the European Society of Cardiology (ESC). Eur Heart J 2009; 30:2631. Copyright &copy; 2009 Oxford University Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
