" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Evaluation of sexual abuse in children and adolescents",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/27/35258/contributors\">",
