var title_f34_27_35260="Influenza symptoms PI";
var content_f34_27_35260=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F56899&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F56899&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Is it a cold or the flu?",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Cold",
"       </td>",
"       <td class=\"subtitle1\">",
"        Flu",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fever",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Usual; high (100&deg;F to 102&deg;F; occasionally higher, especially in young children); lasts 3 to 4 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Headache",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Common",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        General aches, pains",
"       </td>",
"       <td>",
"        Slight",
"       </td>",
"       <td>",
"        Usual; often severe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fatigue, weakness",
"       </td>",
"       <td>",
"        Sometimes",
"       </td>",
"       <td>",
"        Usual; can last up to 2 to 3 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extreme exhaustion",
"       </td>",
"       <td>",
"        Never",
"       </td>",
"       <td>",
"        Usual; at the beginning of the illness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stuffy nose",
"       </td>",
"       <td>",
"        Common",
"       </td>",
"       <td>",
"        Sometimes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sneezing",
"       </td>",
"       <td>",
"        Usual",
"       </td>",
"       <td>",
"        Sometimes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sore throat",
"       </td>",
"       <td>",
"        Common",
"       </td>",
"       <td>",
"        Sometimes",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td class=\"indent1\">",
"        Chest discomfort, cough",
"       </td>",
"       <td>",
"        Mild to moderate; hacking cough",
"       </td>",
"       <td>",
"        Common; can become severe",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Treatment",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Antihistamines",
"        </p>",
"        <p>",
"         Decongestant",
"        </p>",
"        Pain/fever reliever (eg, ibuprofen/Motrin&reg;), naproxen/Aleve&reg;, acetaminophen/Tylenol&reg;",
"       </td>",
"       <td>",
"        <p>",
"         Antiviral medicines - see your doctor",
"        </p>",
"        Pain/fever reliever (eg, ibuprofen/Motrin&reg;), naproxen/Aleve&reg;, acetaminophen/Tylenol&reg;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Prevention",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Wash your hands often",
"        </p>",
"        Avoid close contact with anyone with a cold",
"       </td>",
"       <td>",
"        <p>",
"         Annual vaccination; antiviral medicine - see your doctor",
"        </p>",
"        <p>",
"         Wash your hands often",
"        </p>",
"        Avoid close contact with anyone who has the flu",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Complications",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Sinus congestion",
"        </p>",
"        <p>",
"         Middle ear infection",
"        </p>",
"        <p>",
"         Asthma",
"        </p>",
"        Bronchitis",
"       </td>",
"       <td>",
"        Bronchitis, pneumonia; can be life threatening",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: National Institutes of Health. (file://www.niaid.nih.gov/publications/cold/sick.pdf).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
