" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Functional dyspepsia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/28/35272/contributors\">",
