var title_f34_29_35292="Causes of congenital hypothyroidism";
var content_f34_29_35292=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F68356&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F68356&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Major causes of congenital hypothyroidism, approximate incidence, and serum thyroid function test results",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Cause",
"       </td>",
"       <td class=\"subtitle1\">",
"        Incidence",
"       </td>",
"       <td class=\"subtitle1\">",
"        Serum free T4",
"       </td>",
"       <td class=\"subtitle1\">",
"        Serum T4",
"       </td>",
"       <td class=\"subtitle1\">",
"        Serum TSH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Primary hypothyroidism",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        1:2000 to 1:4000",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        &darr;",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        &darr;",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        &uarr;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Thyroid dysgenesis: ectopia, aplasia, or hypoplasia",
"       </td>",
"       <td class=\"sublist_other\">",
"        85 percent of cases",
"       </td>",
"       <td class=\"sublist_other\">",
"        &darr;",
"       </td>",
"       <td class=\"sublist_other\">",
"        &darr;",
"       </td>",
"       <td class=\"sublist_other\">",
"        &uarr;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Inborn errors of thyroxine synthesis (dyshormonogenesis)",
"       </td>",
"       <td class=\"sublist_other\">",
"        15 percent of cases",
"       </td>",
"       <td class=\"sublist_other\">",
"        &darr;",
"       </td>",
"       <td class=\"sublist_other\">",
"        &darr;",
"       </td>",
"       <td class=\"sublist_other\">",
"        &uarr;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Central hypothyroidism",
"       </td>",
"       <td>",
"        1:25,000-1:100,000",
"       </td>",
"       <td>",
"        &darr;",
"       </td>",
"       <td>",
"        &darr;",
"       </td>",
"       <td>",
"        Normal or &darr;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"5\">",
"        Transient hypothyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Europe - iodine deficiency",
"       </td>",
"       <td class=\"sublist_other\">",
"        1:100",
"       </td>",
"       <td class=\"sublist_other\">",
"        &darr; then normalizes",
"       </td>",
"       <td class=\"sublist_other\">",
"        &darr; then normalizes",
"       </td>",
"       <td class=\"sublist_other\">",
"        &uarr; then normalizes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - North America - iodide excess and other causes",
"       </td>",
"       <td class=\"sublist_other\">",
"        1:50,000",
"       </td>",
"       <td class=\"sublist_other\">",
"        &darr; then normalizes",
"       </td>",
"       <td class=\"sublist_other\">",
"        &darr; then normalizes",
"       </td>",
"       <td class=\"sublist_other\">",
"        &uarr; then normalizes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Maternal antibody-mediated hypothyroidism",
"       </td>",
"       <td class=\"sublist_other\">",
"        1:25,000-1:100,000",
"       </td>",
"       <td class=\"sublist_other\">",
"        &darr;",
"       </td>",
"       <td class=\"sublist_other\">",
"        &darr;",
"       </td>",
"       <td class=\"sublist_other\">",
"        &uarr;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thyroxine-binding globulin deficiency (causes low serum T4 concentrations but not hypothyroidism)",
"       </td>",
"       <td>",
"        1:4300",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        &darr;",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
