" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Chronic obstructive pulmonary disease: Prognostic factors and comorbid conditions",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/33/35351/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/33/35351/contributors\">",
"     Scott T Weiss, MD, MS",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
