var title_f34_33_35357="Asthma trigger questions";
var content_f34_33_35357=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F80507&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F80507&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Assessment questions* for environmental and other factors that can make asthma worse",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Inhalant allergens",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Does the patient have symptoms year round? (If yes, ask the following questions. If no, see next set of questions) .",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Does the patient keep pets indoors? What type?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Does the patient have moisture or dampness in any room of his or her home (eg, basement)? (Suggests house-dust mites, molds).",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Does the patient have mold visible in any part of his or her home? (Suggests molds).",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Has the patient seen cockroaches or rodents in his or her home in the past month? (Suggests significant exposure).",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Assume exposure to house dust mites unless patient lives in a semiarid region. However, if a patient living in a semiarid region uses a swamp cooler, exposure to house dust mites must still be assumed.",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Do symptoms get worse at certain times of the year? (If yes, ask when symptoms occur).",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Early spring? (Trees)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Late spring? (Grasses)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Late summer to autumn? (Weeds)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Summer and fall? (Alternaria, Cladosporium, mites)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Cold months in temperate climates? (Animal dander)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Tobacco smoke",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does the patient smoke?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does anyone smoke at home or work?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does anyone smoke at the child's daycare?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Indoor/outdoor pollutants and irritants",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Is a wood-burning stove or fireplace used in the patient's home?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Are there unvented stoves or heaters in the patient's home?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does the patient have contact with other smells or fumes from perfumes, cleaning agents, or sprays?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Have there been recent renovations or painting in the home?",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Workplace exposures",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does the patient cough or wheeze during the week, but not on weekends when away from work?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Do the patient's eyes and nasal passages get irritated soon after arriving at work?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Do coworkers have similar symptoms?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            What substances are used in the patient's worksite? (Assess for sensitizers).",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Rhinitis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does the patient have constant or seasonal nasal congestion, runny nose, and/or postnasal drip?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Gastroesophageal reflux disease (GERD)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does the patient have heartburn?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does food sometimes come up into the patient's throat?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Has the patient had coughing, wheezing, or shortness of breath at night in the past&nbsp;four weeks?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does the infant vomit, followed by cough, or have wheezy cough at night? Are symptoms worse after feeding?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Sulfite sensitivity",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does the patient have wheezing, coughing, or shortness of breath after eating shrimp, dried fruit, or processed potatoes or after drinking beer or wine?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Medication sensitivities and contraindications",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            What medications does the patient use now (prescription and nonprescription)?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does the patient use eyedrops? What type?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does the patient use any medications that contain beta-blockers?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Does the patient ever take aspirin or other nonsteroidal antiinflammatory drugs?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Has the patient ever had symptoms of asthma after taking any of these medications?",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * These questions are examples and do not represent a standardized assessment or diagnostic instrument. The validity and reliability of these questions have not been assessed.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: National Heart, Blood, and Lung Institute Expert Panel Report 3 (EPR 3): Guidelines for the Diagnosis and Management of Asthma. NIH Publication no. 08-4051, 2007.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
