var title_f34_34_35371="Hepatitis scoring system";
var content_f34_34_35371=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F51662&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F51662&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Scoring system for necroinflammatory activity and fibrosis in chronic hepatitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Grade",
"       </td>",
"       <td class=\"subtitle1\">",
"        Portal/periportal activity",
"       </td>",
"       <td class=\"subtitle1\">",
"        Lobular activity",
"       </td>",
"       <td class=\"subtitle1\">",
"        Fibrosis*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        0",
"       </td>",
"       <td>",
"        None or minimal",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        Portal inflammation (CPH)",
"       </td>",
"       <td>",
"        Inflammation but no necrosis",
"       </td>",
"       <td>",
"        Enlarged fibrotic portal tracts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2",
"       </td>",
"       <td>",
"        Mild piecemeal necrosis (mild CAH)",
"       </td>",
"       <td>",
"        Focal necrosis or acidophil bodies",
"       </td>",
"       <td>",
"        Periportal or portal-portal septa but intact architecture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3",
"       </td>",
"       <td>",
"        Moderate piecemeal necrosis (moderate CAH)",
"       </td>",
"       <td>",
"        Severe focal cell damage",
"       </td>",
"       <td>",
"        Fibrosis with architetural distortion but no cirrhosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4",
"       </td>",
"       <td>",
"        Severe piecemeal necrosis (severe CAH)",
"       </td>",
"       <td>",
"        Damage includes bridging necrosis",
"       </td>",
"       <td>",
"        Probable or definite cirrhosis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     *Alternatively, cirrhosis can be separately scored from fibrosis into the following categories: probably absent; developing, suspected; cannot be assessed.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Scheuer, PJ, J Hepatol 1991; 13:372.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
