" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Prognosis and treatment of cardiogenic shock complicating acute myocardial infarction",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/37/35418/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/37/35418/contributors\">",
"     Venu Menon, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/37/35418/contributors\">",
"     Judith S Hochman, MD",
"    </a>",
"    <br/>",
"   </div>",
