var title_f34_37_35419="Estimating energy expenditure";
var content_f34_37_35419=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F63308&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=0\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F63308&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=0\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Revised World Health Organization equations for estimating energy expenditure",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\" rowspan=\"1\">",
"        Step 1: Estimate basal metabolic rate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        Men 18 to 30 years = (0.0630 x actual weight in kg + 2.8957) x 240 kcal/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        Men 31 to 60 years = (0.0484 x actual weight in kg + 3.6534) x 240 kcal/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        Women 18 to 30 years = (0.0621 x actual weight in kg + 2.0357) x 240 kcal/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        Women 31 to 60 years = (0.0342 x actual weight in kg + 3.5377) x 240 kcal/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\" rowspan=\"1\">",
"        Step 2: Determine activity factor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Activity level",
"       </td>",
"       <td class=\"subtitle2_single\">",
"        Activity factor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Low (sedentary)",
"       </td>",
"       <td>",
"        1.3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intermediate (some regular exercise)",
"       </td>",
"       <td>",
"        1.5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        High (regular activity or demanding job)",
"       </td>",
"       <td>",
"        1.7",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\" rowspan=\"1\">",
"        Step 3: Estimate total energy expenditure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        Total energy expenditure = Basal metabolic rate x activity factor",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
