var title_f34_39_35451="Rx myasthenic crisis";
var content_f34_39_35451=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F82137&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F82137&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of myasthenic crisis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Admit to intensive care unit",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Measure FVC frequently, as often as every two hours if respiratory status is deteriorating",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Electively intubate in the presence of any of the following conditions:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        FVC less than 15 mL/kg body weight",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Declines in serial measurements of FVC approaching 15 mL/kg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Declines in serial measurements of NIF approaching 25 cmH2O",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Clinical signs of respiratory distress",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Difficulty handling oral secretions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Withdraw anticholinesterase medications to reduce airway secretions in patients who are intubated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Begin rapid therapy with plasmapheresis or IVIG to treat myasthenic crisis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Begin immunomodulating therapy with high dose glucocorticoids (eg, prednisone 60 to 80 mg per day). Consider azathioprine, mycophenolate mofetil, or cyclosporine if glucocorticoids are contraindicated or previously ineffective",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Initiate weaning from mechanical ventilation when respiratory muscle strength is improving with plasmapheresis or IVIG treatment, as quantified by a FVC &gt;15 mL/kg and NIF &gt;30 cm H2O",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     FVC: forced vital capacity; IVIG: intravenous immune globulin; NIF: negative inspiratory force.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
