" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Common etiologic agents of pediatric pneumonia*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Microbial agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Susceptible hosts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Bacteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <em>",
"         Streptococcus pneumoniae",
"        </em>",
"       </td>",
"       <td>",
"        All",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <em>",
"         Mycoplasma pneumoniae",
"        </em>",
"       </td>",
"       <td>",
"        Primarily children &ge;5 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <em>",
"         Chlamydophila pneumoniae",
"        </em>",
"       </td>",
"       <td>",
"        Primarily children &ge;5 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <em>",
"         Chlamydia trachomatis",
"        </em>",
"       </td>",
"       <td>",
"        First 3 months of life",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <em>",
"         Treponema pallidum",
"        </em>",
"       </td>",
"       <td>",
"        First 3 months of life",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <em>",
"         Mycoplasma hominis",
"        </em>",
"       </td>",
"       <td>",
"        First 3 months of life",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <em>",
"         Ureaplasma urealyticum",
"        </em>",
"       </td>",
"       <td>",
"        First 3 months of life",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <em>",
"         Staphylococcus aureus",
"        </em>",
"       </td>",
"       <td>",
"        Primarily children &lt;5 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <em>",
"         Streptococcus pyogenes",
"        </em>",
"       </td>",
"       <td>",
"        Primarily children &lt;5 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Viruses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Respiratory syncytial virus",
"       </td>",
"       <td>",
"        Primarily children &lt;5 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Influenza A and B",
"       </td>",
"       <td>",
"        Primarily children &lt;5 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Parainfluenza 1, 2, and 3",
"       </td>",
"       <td>",
"        Primarily children &lt;5 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adenovirus",
"       </td>",
"       <td>",
"        Primarily children &lt;5 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rhinovirus",
"       </td>",
"       <td>",
"        Primarily children &lt;5 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Human metapneumovirus",
"       </td>",
"       <td>",
"        Primarily children &lt;5 years",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
