var title_f34_41_35484="Classification and management of blood pressure";
var content_f34_41_35484=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F72683&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F72683&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification and management of blood pressure for adults aged 18 years or older",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"1\" rowspan=\"3\">",
"        BP classification",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"1\" rowspan=\"3\">",
"        Systolic BP mmHg*",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"1\" rowspan=\"3\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"1\" rowspan=\"3\">",
"        Diastolic BP mmHg*",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"3\" rowspan=\"1\">",
"        Management*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\" colspan=\"1\" rowspan=\"2\">",
"        Lifestyle modification",
"       </td>",
"       <td class=\"subtitle2\" colspan=\"2\" rowspan=\"1\">",
"        Initial drug therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3\">",
"        Without compelling indication",
"       </td>",
"       <td class=\"subtitle3\">",
"        With compelling indications&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        &lt;120",
"       </td>",
"       <td>",
"        and",
"       </td>",
"       <td>",
"        &lt;80",
"       </td>",
"       <td>",
"        Encourage",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prehypertension",
"       </td>",
"       <td>",
"        120-139",
"       </td>",
"       <td>",
"        or",
"       </td>",
"       <td>",
"        80-89",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        No antihypertensive drug indicated",
"       </td>",
"       <td>",
"        Drug(s) for the compelling indications&Delta;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage 1 hypertension",
"       </td>",
"       <td>",
"        140-159",
"       </td>",
"       <td>",
"        or",
"       </td>",
"       <td>",
"        90-99",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        Thiazide-type diuretics for most; may consider ACE inhibitor, ARB, beta blocker, CCB, or combination",
"       </td>",
"       <td>",
"        Drug(s) for the compelling indications; other anti-hypertensive drugs (diuretics, ACE inhibitor, ARB, beta blocker, CCB) as needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage 2 hypertension",
"       </td>",
"       <td>",
"        &ge;160",
"       </td>",
"       <td>",
"        or",
"       </td>",
"       <td>",
"        &ge;100",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        2-drug combination for most (usually thiazide-type diuretic and ACE inhibitor or ARB or beta blocker or CCB)&loz;",
"       </td>",
"       <td>",
"        Drug(s) for the compelling indications; other antihypertensive drugs (diuretics, ACE inhibitor, ARB, beta blocker, CCB) as needed",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ACE: angiotensin-converting enzyme; ARB: angiotensin-receptor blocker; BP: blood pressure; CCB: calcium channel blocker.",
"     <br>",
"      * Treatment determined by highest BP category.",
"      <br>",
"       &Delta; Treat patients with chronic kidney disease or diabetes to BP goal of less than 130/80 mmHg. Other compelling indications include disorders such as heart failure, post-myocardial infarction, and atrial fibrillation in which particular antihypertensive drugs are warranted independent of BP.",
"       <br>",
"        &loz; Initial combined therapy should be used cautiously in those at risk for orthostatic hypotension.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from The Seventh Report of the Joint National Committee on Prevention, Detection, Evaluation, and Treatment of High Blood Pressure. JAMA 2003; 289:2560.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
