" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Severity of mitral regurgitation (MR) in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mild",
"       </td>",
"       <td class=\"subtitle1\">",
"        Moderate",
"       </td>",
"       <td class=\"subtitle1\">",
"        Severe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Structural parameters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Left atrial (LA) size",
"       </td>",
"       <td>",
"        Normal, unless other causes of left atrial dilation",
"       </td>",
"       <td>",
"        Normal or dilated",
"       </td>",
"       <td>",
"        Dilated, except acute MR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Left ventricular size",
"       </td>",
"       <td>",
"        Normal, unless other causes of left ventricular dilation",
"       </td>",
"       <td>",
"        Normal or dilated",
"       </td>",
"       <td>",
"        Dilated, except acute MR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mitral leaflets or support apparatus",
"       </td>",
"       <td>",
"        Normal or abnormal",
"       </td>",
"       <td>",
"        Normal or abnormal",
"       </td>",
"       <td>",
"        Abnormal/flail leaflet, ruptured papillary muscle",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Doppler parameters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Color Doppler jet area",
"       </td>",
"       <td>",
"        Small, central jet, (&lt;4 cm2 or &lt;20 percent of LA area)",
"       </td>",
"       <td>",
"        Greater than mild but no criteria for severe MR",
"       </td>",
"       <td>",
"        Vena contracta width &gt;0.7 cm with large central MR jet (area 40 percent of LA area or a wall impinging jet of any size, swirling in LA",
"       </td>",
