" <!-- TC:TOPIC_PAGE -->",
" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Complications of chronic pancreatitis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/47/35572/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/47/35572/contributors\">",
"     Steven D Freedman, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/47/35572/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/47/35572/contributors\">",
"     David C Whitcomb, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/47/35572/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/47/35572/contributors\">",
"     Shilpa Grover, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"mobipreview.htm?34/47/35572/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
"  <div id=\"reviewProcess\">",
