var title_f34_48_35595="New daily persistent headache criteria";
var content_f34_48_35595=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F76010&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F76010&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    New daily persistent headache diagnostic criteria",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <strong>",
"         Description:",
"        </strong>",
"        Headache that is daily and unremitting from very soon after onset (within three days at most). The pain is typically bilateral, pressing or tightening in quality and of mild to moderate intensity. There may be photophobia, phonophobia or mild nausea.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Diagnostic criteria:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        A. Headache for more than three months fulfilling criteria B through D",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        B. Headache is daily and unremitting from onset or from less than three days from onset",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        C. At least two of the following pain characteristics:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        1. Bilateral location",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        2. Pressing/tightening (nonpulsating) quality",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        3. Mild or moderate intensity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        4. Not aggravated by routine physical activity such as walking or climbing stairs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        D. Both of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        1. No more than one of photophobia, phonophobia or mild nausea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        2. Neither moderate or severe nausea nor vomiting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        E. Not attributed to another disorder",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Headache classification subcommittee of the International Headache Society. The International Classification of Headache Disorders: 2nd edition. Cephalalgia 2004; 24 Suppl 1:9.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
