var title_f34_48_35596="Rx Chlamydia trachomatis";
var content_f34_48_35596=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F76844&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F76844&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment options for chlamydia trachomatis infection and related clinical syndromes",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Urethritis, cervicitis, conjunctivitis, or proctitis (except lymphogranuloma venereum)*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Drugs of choice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Azithromycin 1 g oral once",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Doxycycline&bull;&Delta; 100 mg oral twice daily for seven days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Alternatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ofloxacin&Delta; 300 mg oral twice daily for seven days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Levofloxacin&Delta; 500 mg oral daily for seven days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Erythromycin&loz; 500 mg oral four times daily for seven days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Infection in pregnancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Drugs of choice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Azithromycin 1 g oral once",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Amoxicillin 500 mg oral three times daily for seven days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Alternatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Erythromycin&loz; 500 mg oral four times daily for seven days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Neonatal ophthalmia or pneumonia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Drugs of choice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Azithromycin 20 mg/kg oral once daily for three days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Alternatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Erythromycin 12.5 mg/kg oral four times daily for 14 days&sect;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Lymphogranuloma venereum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Drugs of choice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Doxycycline&bull;&Delta; 100 mg oral twice daily for 21 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Alternatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Erythromycin&loz; 500 mg oral four times daily for 21 days",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Related clinical syndromes include nonchlamydial nongonococcal urethritis and cervicitis.",
"     <br>",
"      &bull; Or oral tetracycline 500 mg four times daily.",
"      <br>",
"       &Delta; Not recommended in pregnancy.",
"       <br>",
"        &loz; Erythromycin ethylsuccinate 800 mg may be substituted for erythromycin base 500 mg; erythromycin estolate is contraindicated in pregnancy.",
"        <br>",
"         &sect; Pyloric stenosis has been associated with use of erythromycin in newborns.",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Drugs for sexually transmitted diseases. Treat Guidel Med Lett 2004; 2:67.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
