var title_f34_50_35628="Parenteral rx elective PCI";
var content_f34_50_35628=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F64612&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F64612&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Parenteral antithrombotic drugs for elective percutaneous coronary intervention (PCI)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Initiation relative to PCI",
"       </td>",
"       <td class=\"subtitle1\">",
"        Duration of therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Abciximab",
"       </td>",
"       <td>",
"        0.25 mg/kg IV bolus, then 0.125 mcg/kg/min IV (max 10 mcg/min).",
"       </td>",
"       <td rowspan=\"2\">",
"        10 to 60 min prior to PCI",
"       </td>",
"       <td rowspan=\"2\">",
"        12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Renal adjustment: none",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Eptifibatide",
"       </td>",
"       <td>",
"        180 mcg/kg IV bolus x2, 10 min apart (max 22.6 mg each bolus). Start infusion of 2 mcg/kg/min (max 15 mg/hr) after first bolus.",
"       </td>",
"       <td rowspan=\"3\">",
"        Immediately prior to PCI",
"       </td>",
"       <td rowspan=\"3\">",
"        12 to 24 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Renal adjustment of continuous infusion: CrCl* &lt;50 mL/min: 1 mcg/kg/min (max 7.5 mg/hr)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hemodialysis dependent: contraindicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tirofiban",
"       </td>",
"       <td>",
"        Not generally used in elective PCI",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Unfractionated heparin",
"       </td>",
"       <td>",
"        60 to 100 units/kg IV bolus, titrate to ACT 250 to 300 sec (50 to 70 units/kg IV if GP IIb/IIIa used, titrate to ACT 200 to 250 sec).",
"       </td>",
"       <td rowspan=\"2\">",
"        Immediately prior to PCI",
"       </td>",
"       <td rowspan=\"2\">",
"        No additional heparin post-PCI",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Renal adjustment: none",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Enoxaparin",
"       </td>",
"       <td>",
"        Not generally started for elective PCI. For the occasional patient already on drug, it can be continued at prior dose with an additional 0.3 mg/kg IV if 8 to 12 hours since last dose.",
"       </td>",
"       <td rowspan=\"2\">",
"        &nbsp;",
"       </td>",
"       <td rowspan=\"2\">",
"        May continue for prior indication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Renal adjustment: avoid use if CrCl* &lt;30 mL/min or dialysis dependent.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Bivalirudin",
"       </td>",
"       <td>",
"        0.75 mg/kg IV bolus, then 1.75 mg/kg/hr IV.",
"       </td>",
"       <td rowspan=\"3\">",
"        Immediately prior to PCI",
"       </td>",
"       <td rowspan=\"3\">",
"        Until end of PCI. Continuation of the bivalirudin infusion following PCI for up to four hours post-procedure is optional, at the discretion of the treating physician.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Renal adjustment of continuous infusion: CrCl* 10 to 29 mL/min: 1 mg/kg/hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hemodialysis dependent: 0.25 mg/kg/hr",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * CrCl is estimated using Cockcroft-Gault equation. For discussion of Cockcroft-Gault equation, see \"Assessment of kidney function: Serum creatinine; BUN; and GFR\", section on Estimation equations.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: Antiplatelet and anticoagulant drugs. The Medical Letter 2008; 6:29.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
