" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Uncommon causes of congenital adrenal hyperplasia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/51/35641/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/51/35641/contributors\">",
"     Lynnette K Nieman, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/51/35641/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/51/35641/contributors\">",
"     Andre Lacroix, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/51/35641/contributors\">",
"     John L Kirkland, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/51/35641/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/51/35641/contributors\">",
"     Kathryn A Martin, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"mobipreview.htm?34/51/35641/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
"  <div id=\"reviewProcess\">",
"   <span>",
"    All topics are updated as new evidence becomes available and our",
"   </span>",
