var title_f34_54_35691="Summary of tests for blunt aortic injury";
var content_f34_54_35691=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F62204&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F62204&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Summary of tests for blunt aortic injury",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        Diagnostic tests for aortic rupture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Plain radiograph",
"       </td>",
"       <td>",
"        <p>",
"         Upright preferable; sensitivity of supine unclear",
"        </p>",
"        <p>",
"         Normal PA radiograph has high negative predictive value; good test for patients with low to moderate suspicion",
"        </p>",
"        <p>",
"         If high clinical suspicion, or abnormal radiograph, further testing required",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chest CT scan",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Test of choice",
"         </strong>",
"        </p>",
"        <p>",
"         Highly sensitive and specific",
"        </p>",
"        <p>",
"         Requires IV contrast",
"        </p>",
"        <p>",
"         Can usually proceed directly to OR with a positive CT",
"        </p>",
"        <p>",
"         Equivocal study necessitates angiography",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Angiography",
"       </td>",
"       <td>",
"        <p>",
"         Highly sensitive and specific",
"        </p>",
"        <p>",
"         Mainly used for equivocal CT scans",
"        </p>",
"        <p>",
"         Rarely adds value in the setting of a diagnostic CT and delays potentially life-saving operative intervention",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Transesphosageal echocardiography",
"       </td>",
"       <td>",
"        <p>",
"         Highly accurate",
"        </p>",
"        <p>",
"         Can be performed at bedside, in OR, or in patients who cannot tolerate contrast",
"        </p>",
"        <p>",
"         Limited in proximal ruptures, but overall an excellent test when CT is unavailable or contraindicated",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
