" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Hereditary primary motor sensory neuropathies, including Charcot-Marie-Tooth disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/59/35770/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/59/35770/contributors\">",
"     Robert P Cruse, DO",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/59/35770/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/59/35770/contributors\">",
"     Douglas R Nordli, Jr, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/59/35770/contributors\">",
"     Helen V Firth, DM, FRCP, DCH",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?34/59/35770/contributors\">",
"     Jeremy M Shefner, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/59/35770/contributors\">",
