var title_f34_59_35773="1996 1999 2007 recs ACIP";
var content_f34_59_35773=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F72958&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F72958&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Summary of recommendations of the Advisory Committee on Immunization Practices (ACIP) for prevention of varicella-United States, 1996, 1999, and 2007",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Category",
"       </td>",
"       <td class=\"subtitle1\">",
"        1996 recommendations",
"       </td>",
"       <td class=\"subtitle1\">",
"        1999 recommendations",
"       </td>",
"       <td class=\"subtitle1\">",
"        2007 recommendations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Routine childhood schedules",
"        </strong>",
"       </td>",
"       <td>",
"        1 dose recommended at age 12-18 months",
"       </td>",
"       <td>",
"        No change",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          2 doses recommended:",
"         </strong>",
"        </p>",
"        <ul>",
"         <li>",
"          1st dose at age 12-15 months",
"         </li>",
"         <li>",
"          2nd dose at age 4-6 years",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        <strong>",
"         Adults and adolescents aged &ge;13 years",
"        </strong>",
"       </td>",
"       <td>",
"        2 doses, 4-8 weeks apart",
"       </td>",
"       <td>",
"        2 doses, 4-8 weeks apart",
"       </td>",
"       <td>",
"        2 doses, 4-8 weeks apart",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         <strong>",
"          Recommended for susceptible persons who have close contact with persons at high risk for serious complications:",
"         </strong>",
"        </p>",
"        <ul>",
"         <li>",
"          Health-care workers",
"         </li>",
"         <li>",
"          Family contacts of immunocompromised persons",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        No change",
"       </td>",
"       <td rowspan=\"3\">",
"        Recommended for all adolescents and adults without evidence of immunity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         <strong>",
"          Should be considered for susceptible persons at high risk for exposure:",
"         </strong>",
"        </p>",
"        <ul>",
"         <li>",
"          Persons who live or work in environments in which transmission of VZV is likely (eg, teachers of young children, child care employees, and residents and staff members in institutional settings)",
"         </li>",
"         <li>",
"          Persons who live and work in environments in which transmission can occur (eg, college students, inmates and staff members of correctional institutions, and military personnel)",
"         </li>",
"         <li>",
"          Nonpregnant women of childbearing age",
"         </li>",
"         <li>",
"          International travelers",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Recommended for susceptible persons at high risk for exposure or transmission:",
"         </strong>",
"        </p>",
"        <ul>",
"         <li>",
"          Persons who live or work in environments in which transmission of VZV is likely (eg, teachers of young children, day care employees, and residents and staff members in institutional settings)",
"         </li>",
"         <li>",
"          Persons who live and work in environments in which transmission can occur (eg, college students, inmates and staff members of correctional institutions, and military personnel)",
"         </li>",
"         <li>",
"          Nonpregnant women of childbearing age",
"         </li>",
"         <li>",
"          International travelers",
"         </li>",
"         <li>",
"          Adolescents and adults living in households with children",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Is desirable for other susceptible adolescents",
"       </td>",
"       <td>",
"        No change",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Catch-up vaccination",
"        </strong>",
"       </td>",
"       <td>",
"        1 dose recommended for all susceptible children aged 19 months-12 years (ie, those with no history of varicella or vaccination)",
"       </td>",
"       <td>",
"        No change",
"       </td>",
"       <td>",
"        2nd dose recommended for all persons who received 1 dose previously",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        <strong>",
"         HIV*-infected persons",
"        </strong>",
"       </td>",
"       <td rowspan=\"3\">",
"        Contraindicated",
"       </td>",
"       <td>",
"        2 doses, 3 months apart",
"       </td>",
"       <td>",
"        2 doses, 3 months apart",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Should be considered for asymptomatic or mildly symptomatic HIV-infected children in CDC immunologic and clinical categories N1 or A1 with age-specific CD4+ T-lymphocyte percentages &ge;25 percent",
"       </td>",
"       <td>",
"        Should be considered for HIV-infected children with age-specific CD4+ T-lymphocyte, lymphocyte percentages &ge;15 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May be considered for adolescents and adults with CD4 counts &ge;200/microL",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Antenatal screening",
"        </strong>",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        Recommended prenatal assessment and postpartum vaccination",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Outbreak control vaccination",
"        </strong>",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        Should be considered",
"       </td>",
"       <td>",
"        Recommended 2-dose vaccination policy",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Postexposure vaccination",
"        </strong>",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        Recommended within 3-5 days",
"       </td>",
"       <td>",
"        No change",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        <strong>",
"         Vaccination requirements",
"        </strong>",
"       </td>",
"       <td rowspan=\"2\">",
"        None",
"       </td>",
"       <td>",
"        Recommended for children without evidence of immunity attending child care centers and entering elementary school",
"       </td>",
"       <td rowspan=\"2\">",
"        Recommended for children attending child care centers, students in all grade levels, and persons attending college or other postsecondary educational institutions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Should be considered for middle school and junior high school students without other evidence of immunity",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Human immunodeficiency virus.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Marin M, Guris DG, Chaves SS, et al. Prevention of varicella: recommendations of the Advisory Committee on Immunization Practices (ACIP). MMWR Recomm Rep 2007; 56(RR-4):1-40.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
