var title_f34_6_34924="Progesterone supplementation to prevent preterm birth";
var content_f34_6_34924=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F66456&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F66456&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for progesterone supplementation to prevent preterm birth",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Indication",
"       </td>",
"       <td class=\"subtitle1\">",
"        Progesterone supplementation indicated?",
"       </td>",
"       <td class=\"subtitle1\">",
"        Management",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Singleton pregnancy, prior spontaneous singleton preterm birth, normal cervical length",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        <p>",
"         Hydroxyprogesterone caproate 250 mg intramuscularly weekly beginning between 16 and 20 weeks of gestation and continuing through 36 weeks of gestation or until delivery and monitor cervical length.",
"        </p>",
"        Short (&lt;25 mm) cervix &rarr; perform cerclage",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Singleton pregnancy, prior spontaneous twin preterm birth, normal cervical length",
"       </td>",
"       <td>",
"        Possibly",
"       </td>",
"       <td>",
"        <p>",
"         Hydroxyprogesterone caproate 250 mg intramuscularly weekly beginning between 16 and 20 weeks of gestation and continuing through 36 weeks of gestation or until delivery and monitor cervical length.",
"        </p>",
"        Short (&lt;25 mm) cervix &rarr; perform cerclage",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Singleton pregnancy, no prior spontaneous preterm birth, short cervix (&le;20 mm)",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        <p>",
"         Progesterone suppository 90 to 200 mg vaginally each night from time of diagnosis through 36 weeks of gestation.",
"        </p>",
"        <p>",
"         A vaginal suppository can be prepared by a compounding pharmacy utilizing a commercially available standardized kit.",
"         <sup>",
"          [1]",
"         </sup>",
"        </p>",
"        Other options include a 100 mg micronized progesterone vaginal tablet or an 8 percent vaginal gel containing 90 mg micronized progesterone per dose. Both preparations are commercially available in US, but not approved for prevention of preterm birth in cervical shortening.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Multiple pregnancy (twins or triplets) without prior preterm birth, normal cervical length",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        No progesterone, no cerclage",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Twins, prior preterm birth",
"       </td>",
"       <td>",
"        Possibly",
"       </td>",
"       <td>",
"        Hydroxyprogesterone caproate 250 mg intramuscularly weekly beginning between 16 and 20 weeks of gestation and continuing through 36 weeks of gestation or until delivery.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Twins, short cervix",
"       </td>",
"       <td>",
"        Possibly",
"       </td>",
"       <td>",
"        Vaginal progesterone, no cerclage",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Preterm premature rupture of membranes",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        &ndash;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Positive fetal fibronectin test",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        &ndash;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Undelivered after an episode of preterm labor",
"       </td>",
"       <td>",
"        Unclear",
"       </td>",
"       <td>",
"        &ndash;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <p>",
"     &nbsp;",
"    </p>",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     <p>",
"      &nbsp;",
"     </p>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
