"if(Age_Months >= 240 && Age_Months < 241){L = -2.342796948; M = 21.72190973; S = 0.153240872;};",
"BMI =  Weight / power((Height / 100), 2);",
"",
"if (doCalc) BMI_param.value = fixDP(BMI, dp);",
"",
"",
"",
"Z_Score =  (power((BMI / M), L) - 1) / (L * S);",
"",
"if (doCalc) Z_Score_param.value = fixDP(Z_Score, dp);",
"",
"",
"",
"Percentile =  ZtoPercentile(Z_Score);",
"",
"if (doCalc) Percentile_param.value = fixDP(Percentile, dp);",
"",
"",
"",
"",
"if (doCalc){",
"",
"rrclr();",
"if (Percentile < 5){ document.getElementById('rr1_1').bgColor = \"#cccccc\";",
"}",
"if ((Percentile >= 5) && (Percentile < 85)){ document.getElementById('rr1_2').bgColor = \"#cccccc\";",
"}",
"if ((Percentile >= 85) && (Percentile < 95)){ document.getElementById('rr1_3').bgColor = \"#cccccc\";",
"}",
"if (Percentile >= 95){ document.getElementById('rr1_4').bgColor = \"#cccccc\";",
"}",
"",
"",
"",
"}",
