" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Neonatal epileptic syndromes",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?34/60/35783/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?34/60/35783/contributors\">",
"     Eli M Mizrahi, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?34/60/35783/contributors\">",
