"  </h1>",
"  <div id=\"outline\">",
"   <ul>",
"    <li>",
"     <a class=\"sr_button\" href=\"#H22\" id=\"summRecButton\">",
"      <span>",
"       SUMMARY &amp; RECOMMENDATIONS",
"      </span>",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H1\">",
"      INTRODUCTION",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
