var title_f34_60_35788="Urodynamic test options";
var content_f34_60_35788=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F69931&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F69931&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Urodynamic test options",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type of UI",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism",
"       </td>",
"       <td class=\"subtitle1\">",
"        Associated factors",
"       </td>",
"       <td class=\"subtitle1\">",
"        Diagnostic test options",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Urge",
"       </td>",
"       <td>",
"        Unstable bladder or detrusor instability (DI)",
"       </td>",
"       <td>",
"        No neurologic deficit",
"       </td>",
"       <td>",
"        Simple or multichannel CMG with or without EMG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Detrusor hyperreflexia (DH), detrusor sphincter",
"dyssynergia (DSD)",
"       </td>",
"       <td>",
"        With neurologic lesion such as stroke, supraspinal cord",
"lesion, multiple sclerosis",
"       </td>",
"       <td>",
"        Multichannel CMG with or without EMG; video-urodynamics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Detrusor hyperactivity with impaired contractility",
"(DHIC)",
"       </td>",
"       <td>",
"        Elderly, usually also associated with obstructive or",
"stress symptoms",
"       </td>",
"       <td>",
"        Multichannel CMG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Stress",
"       </td>",
"       <td>",
"        Impaired urethral support (female)",
"       </td>",
"       <td>",
"        Damage to pelvic muscles, nerves, and/or endofascia",
"       </td>",
"       <td>",
"        <p>",
"         Provocative stress test (direct visualization)",
"        </p>",
"        <p>",
"         Simple or multichannel CMG (to exclude",
"DI)",
"        </p>",
"        <p>",
"         UPP or leak point pressure",
"        </p>",
"        <p>",
"         Videourodynamics",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intrinsic sphincter deficiency (ISD)",
"       </td>",
"       <td>",
"        Postoperative (after prostatectomy or anti-incontinence",
"surgery), trauma, aging, radiation, congenital (epispadias)",
"       </td>",
"       <td>",
"        Same as above",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Neurogenic sphincter deficiency",
"       </td>",
"       <td>",
"        Neurogenic, sacral, or infrasacral lesion (eg,",
"myelomeningocele)",
"       </td>",
"       <td>",
"        <p>",
"         Same as above plus",
"        </p>",
"        <p>",
"         EMG",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Overflow",
"       </td>",
"       <td>",
"        Overflow from under-active or acontractile detrusor",
"       </td>",
"       <td>",
"        Neurogenic (low spinal cord lesion, neuropathy,",
"postradical pelvic surgery), idiopathic detrusor failure",
"       </td>",
"       <td>",
"        <p>",
"         Uroflowmetry",
"        </p>",
"        <p>",
"         Multichannel CMG and pressure flow with EMG",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Overflow from outlet obstruction",
"       </td>",
"       <td>",
"        <p>",
"         Male: prostate gland disease, urethral stricture",
"        </p>",
"        <p>",
"         Female: postoperative",
"        </p>",
"       </td>",
"       <td>",
"        Uroflow, multichannel CMG, and pressure flow study",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     The urodynamic tests listed are not recommended for routine use but are options for patients who require further evaluation (see text).",
"     <br>",
"      CMG: cystometrogram; EMG: electromyogram; PVR: postvoid residual; UPP: urethral pressure profilometry.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Fantl, JA, Newman, DK, Colling, J, et al, AHCPR Publication No. 96-0682. Public Health Service, Agency for Health Care Policy and Research, Rockville, MD.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f34_60_35788=[""].join("\n");
var outline_f34_60_35788=null;
var title_f34_60_35789="Cocaine toxicity - Rapid overview";
var content_f34_60_35789=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F70334&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F70334&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cocaine toxicity: Rapid overview",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <span style=\"color: #ff0000;\">",
"         To obtain emergent consultation with a medical toxicologist, call the United States Poison Control Network at 1-800-222-1222, or access the World Health Organization's list of international poison centers (",
"        </span>",
"        <a href=\"file://www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html\" target=\"_blank\">",
"         www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html",
"        </a>",
"        <span style=\"color: #ff0000;\">",
"         ).",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        General",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cocaine is a stimulant (sympathomimetic) that increases energy and produces euphoria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cocaine produces toxicity in virtually every organ system, principally via hemodynamic effects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clinical presentation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        History",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cocaine may produce toxicity in virtually any organ; few complaints (particularly focal complaints) can be dismissed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chest pain, shortness of breath, headache, focal neurological symptoms, and extremity symptoms are particularly worrisome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Physical examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vital signs: Hypertension and tachycardia almost universal; hyperthermia may occur",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        CNS: Agitation common; focal signs suggest cerebrovascular accident",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pupils: Mydriasis common",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lungs: Decreased breath sounds after smoking crack suggest pneumothorax",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extremities: Decreased pulses suggest vascular catastrophe, such as aortic dissection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Laboratory evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        General laboratory screening for poisoned patients:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fingerstick glucose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Electrocardiogram (ECG)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acetaminophen and salicylate levels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Urine pregnancy test in women of childbearing age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Urine toxicology testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Confirms cocaine use in last several days but does",
"        <strong>",
"         not",
"        </strong>",
"        confirm acute toxicity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Advanced testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Driven by clinical symptoms (eg, cardiac biomarkers for suspected myocardial infarction; computerized tomography for suspected thoracic aortic dissection)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Airway management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Succinylcholine relatively contraindicated in rapid sequence intubation; consider rocuronium (1 mg/kg IV) or other nondepolarizing agent as alternative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Psychomotor agitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Administer benzodiazepines (eg, diazepam 5 to 10 mg IV every 3 to 5 minutes until agitation controlled)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Severe or symptomatic hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Administer diazepam (5 mg IV) or lorazepam (1 mg IV); may repeat q 5 minutes until sedated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Phentolamine (1 to 5 mg IV); repeat as necessary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        DO NOT ADMINISTER BETA-BLOCKERS, INCLUDING LABETALOL. Beta-blockade is contraindicated in acute cocaine toxicity.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Cocaine-associated myocardial ischemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Obtain ECG (accuracy with cocaine-associated chest pain unclear; see text)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Administer diazepam (5 mg IV) or lorazepam (1 mg IV) for agitation or hypertension; may repeat as necessary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aspirin 325 mg PO (assuming aortic dissection not suspected)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nitroglycerin 0.4 mg SL with or without continuous infusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Phentolamine 1 to 5 mg IV, repeat as necessary; hold for SBP &lt;100",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        DO NOT ADMINISTER BETA-BLOCKERS, INCLUDING LABETALOL. Beta-blockade is contraindicated in acute cocaine toxicity.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        QRS widening on ECG (rare; suggests profound toxicity)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Administer sodium bicarbonate, 1 to 2 mEq/kg IV push",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Disposition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients with uncomplicated cocaine toxicity (agitation and sympathomimetic toxicity but no evidence of end-organ damage) may be treated with benzodiazepines and observed until symptoms resolve, then discharged.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients with cocaine-associated chest pain and a normal or unchanged electrocardiogram, in whom thoracic aortic dissection is not a concern, should be observed for 9 to 12 hours. If repeat cardiac biomarkers and a repeat electrocardiogram are normal, such patients may be discharged.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients with any evidence of end-organ toxicity should be admitted.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
