var title_f34_61_35803="Immunocompromised pyo rx";
var content_f34_61_35803=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F64708&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F64708&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Empiric antibiotic therapy for pyomyositis in immunocompromised adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Vancomycin should be combined with one of the following regimens for empiric gram-negative and anaerobic coverage:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Monotherapy with a beta-lactam/beta-lactamase inhibitor, such as one of the following:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Ampicillin-sulbactam (3 g every six hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Piperacillin/tazobactam (4.5 g every eight hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Ticarcillin-clavulanate (3.1 g every four hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       A third generation cephalosporin such as ceftriaxone (1 g IV every 24 hours)",
"       <strong>",
"        PLUS",
"       </strong>",
"       metronidazole (500 mg IV every eight hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Alternative empiric regimens for empiric gram-negative and anaerobic coverage include:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       A fluoroquinolone (eg, ciprofloxacin 400 mg IV every 12 hours or levofloxacin 500 mg IV daily)",
"       <strong>",
"        PLUS",
"       </strong>",
"       metronidazole (500 mg IV every eight hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Monotherapy with a carbapenem*, such as one of the following:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Imipenem (500 mg every six hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Meropenem (1 g every 8 hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Ertapenem (1 g daily)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Carbapenems should not be used for patients with history of immediate-type hypersensitivity to beta-lactams.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
