var title_f34_63_35835="Effects of OA PI";
var content_f34_63_35835=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F77930&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F77930&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Effects of osteoarthritis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Age when symptoms start",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Usually after 40",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Commonly affected body parts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Neck and lower back",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Joint at the base of the thumb",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Knuckles in the middle and near the tip of the fingers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hip",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Knee",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Joint in the foot, above the heel",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Knuckle at the base of the big toe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Less commonly affected body parts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Shoulder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wrist",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Elbow",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Knuckles at the base of the fingers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stiffness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Crackling or clicking sounds in the joints",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Extra bone growth (for example, knuckles that look swollen or knobby)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased range of motion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Problems with the alignment of certain joints",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tenderness to the touch",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
