"             <option value=\"1.666666667e-05|0|mL/hr\">",
"              mL/hr",
"             </option>",
"             <option value=\"0.001|0|mL/min\">",
"              mL/min",
"             </option>",
"             <option value=\"0.06|0|mL/sec\">",
"              mL/sec",
"             </option>",
"            </select>",
"           </td>",
"          </tr>",
"         </table>",
"        </center>",
"       </td>",
"       <td align=\"left\" bgcolor=\"#eeeeee\">",
